import React, { useState, useCallback, useRef } from 'react';
import {
  View,
  Text,
  FlatList,
  StyleSheet,
  Image,
  TouchableOpacity,
  Modal,
  TextInput,
  ActivityIndicator,
  Alert,
} from 'react-native';
import firestore from '@react-native-firebase/firestore';
import * as Animatable from 'react-native-animatable';
import { useFocusEffect } from '@react-navigation/native';

export default function MyRecipesScreen() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedItem, setSelectedItem] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);

  const [editTitle, setEditTitle] = useState('');
  const [editDesc, setEditDesc] = useState('');
  const [editImage, setEditImage] = useState('');

  const animationRefs = useRef([]);

  const fetchRecipes = async () => {
    setLoading(true);
    try {
      const snapshot = await firestore().collection('resep').orderBy('createdAt', 'desc').get();
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setRecipes(data);
    } catch (err) {
      console.error('Error fetching:', err);
    } finally {
      setLoading(false);
    }
  };

  useFocusEffect(
    useCallback(() => {
      fetchRecipes();
    }, [])
  );

  const openModal = (item) => {
    setSelectedItem(item);
    setEditTitle(item.title);
    setEditDesc(item.description);
    setEditImage(item.image);
    setModalVisible(true);
  };

  const handleDelete = (id) => {
    Alert.alert('Hapus Resep', 'Apakah kamu yakin ingin menghapus resep ini?', [
      { text: 'Batal', style: 'cancel' },
      {
        text: 'Hapus',
        style: 'destructive',
        onPress: async () => {
          try {
            await firestore().collection('resep').doc(id).delete();
            fetchRecipes();
            setModalVisible(false);
          } catch (error) {
            Alert.alert('Gagal', 'Tidak bisa menghapus resep');
          }
        },
      },
    ]);
  };

  const handleUpdate = async () => {
    if (!editTitle || !editDesc || !editImage) {
      Alert.alert('Validasi', 'Semua field wajib diisi!');
      return;
    }

    try {
      await firestore().collection('resep').doc(selectedItem.id).update({
        title: editTitle,
        description: editDesc,
        image: editImage,
        updatedAt: firestore.FieldValue.serverTimestamp(),
      });
      fetchRecipes();
      setModalVisible(false);
    } catch (err) {
      Alert.alert('Gagal', 'Gagal memperbarui resep.');
    }
  };

  const renderItem = ({ item, index }) => (
    <Animatable.View
      ref={(ref) => (animationRefs.current[index] = ref)}
      animation="fadeInUp"
      delay={index * 150}
      duration={500}
      useNativeDriver
    >
      <TouchableOpacity style={styles.card} onPress={() => openModal(item)}>
        <Image source={{ uri: item.image }} style={styles.image} />
        <View style={styles.cardContent}>
          <Text style={styles.title}>{item.title}</Text>
          <Text style={styles.description} numberOfLines={2}>
            {item.description}
          </Text>
        </View>
      </TouchableOpacity>
    </Animatable.View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Resepku üçΩÔ∏è</Text>

      {loading ? (
        <ActivityIndicator size="large" color="#FF6F91" />
      ) : (
        <FlatList
          data={recipes}
          keyExtractor={(item) => item.id}
          renderItem={renderItem}
          contentContainerStyle={styles.listContent}
          showsVerticalScrollIndicator={false}
        />
      )}

      {/* Modal Edit + Detail */}
      <Modal visible={modalVisible} transparent animationType="slide">
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <TextInput
              style={styles.modalInput}
              placeholder="Judul"
              value={editTitle}
              onChangeText={setEditTitle}
            />
            <TextInput
              style={[styles.modalInput, { height: 80 }]}
              placeholder="Deskripsi"
              value={editDesc}
              onChangeText={setEditDesc}
              multiline
            />
            <TextInput
              style={styles.modalInput}
              placeholder="URL Gambar"
              value={editImage}
              onChangeText={setEditImage}
            />
            <Image source={{ uri: editImage }} style={styles.modalImage} />

            <View style={styles.modalButtonRow}>
              <TouchableOpacity
                style={[styles.button, { backgroundColor: '#FF6F91' }]}
                onPress={handleUpdate}
              >
                <Text style={styles.buttonText}>Simpan</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={[styles.button, { backgroundColor: '#999' }]}
                onPress={() => handleDelete(selectedItem.id)}
              >
                <Text style={styles.buttonText}>Hapus</Text>
              </TouchableOpacity>
            </View>

            <TouchableOpacity onPress={() => setModalVisible(false)}>
              <Text style={styles.closeButtonText}>Tutup</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </View>
  );
}

